import{_ as a,c as t,o as i,aj as o}from"./chunks/framework.lC5GZWV9.js";const p=JSON.parse('{"title":"Video games","description":"","frontmatter":{},"headers":[],"relativePath":"guides/video-games.md","filePath":"guides/video-games.md","lastUpdated":1772193071000}'),r={name:"guides/video-games.md"};function n(l,e,s,d,h,c){return i(),t("div",null,[...e[0]||(e[0]=[o('<h1 id="video-games" tabindex="-1">Video games <a class="header-anchor" href="#video-games" aria-label="Permalink to “Video games”">​</a></h1><p>A guide about video games integration for Ryot.</p><h2 id="integration-with-igdb" tabindex="-1">Integration with IGDB <a class="header-anchor" href="#integration-with-igdb" aria-label="Permalink to “Integration with IGDB”">​</a></h2><p>Ryot supports tracking video games via <a href="https://www.igdb.com" target="_blank" rel="noreferrer">IGDB</a>. You can follow the below steps to obtain your own API keys and enable video game tracking.</p><h3 id="steps" tabindex="-1">Steps <a class="header-anchor" href="#steps" aria-label="Permalink to “Steps”">​</a></h3><ol><li>Create a <a href="https://twitch.tv" target="_blank" rel="noreferrer">Twitch</a> account.</li><li>Open your <a href="https://dev.twitch.tv/console" target="_blank" rel="noreferrer">developer console</a>.</li><li>Click on &quot;Register Your Application&quot; on the dashboard.</li><li>Fill up the details. Any name will suffice but it must be unique. Click on &quot;Create&quot; when you are done.</li><li>You will be guided back to your application dashboard. Click on &quot;Manage&quot; for the application you just created.</li><li>Generate a client secret. Copy the <strong>Client ID</strong> and <strong>Client Secret</strong>.</li><li>Set the <code>VIDEO_GAMES_*</code> environment variables as described in the <a href="./../configuration.html#important-parameters">configuration</a> docs.</li></ol><h2 id="integration-with-giantbomb" tabindex="-1">Integration with GiantBomb <a class="header-anchor" href="#integration-with-giantbomb" aria-label="Permalink to “Integration with GiantBomb”">​</a></h2><p>Ryot also supports tracking video games via <a href="https://www.giantbomb.com" target="_blank" rel="noreferrer">GiantBomb</a>. This provides an alternative source for video game metadata with comprehensive coverage.</p><h3 id="steps-1" tabindex="-1">Steps <a class="header-anchor" href="#steps-1" aria-label="Permalink to “Steps”">​</a></h3><ol><li>Create a <a href="https://www.giantbomb.com" target="_blank" rel="noreferrer">GiantBomb</a> account.</li><li>Navigate to your <a href="https://www.giantbomb.com/api" target="_blank" rel="noreferrer">API key page</a>.</li><li>Generate an API key if you don&#39;t have one already.</li><li>Copy the <strong>API Key</strong>.</li><li>Set the <code>VIDEO_GAMES_GIANT_BOMB_API_KEY</code> environment variable with your API key.</li></ol>',10)])])}const m=a(r,[["render",n]]);export{p as __pageData,m as default};
